mapper.insert:
INSERT INTO shopping_list(
    s_no, id, p_no, ea
) VALUES (
    shopping_list_seq.NEXTVAL, #{Id}, #{p_no}, #{ea}
)

mapper.select:
SELECT NVL(SUM(price * ea), 0) money
FROM shopping_list c, product p
WHERE c.p_no = p.p_no AND c.id = #{Id}

mapper.update:
UPDATE shopping_list    
SET total_amt = #{total_Amt} 
WHERE id= #{Id} 
AND p_no = #{p_No}

mapper.select:
SELECT COUNT(*)
FROM shopping_list
WHERE id= #{iD} 
AND p_no = #{p_No}

mapper.update:
UPDATE shopping_list 
SET total_amt = total_amt + #{total_amt} 
WHERE id= #{iD} 
AND p_No = #{p_No}
