mapper.select:
SELECT seq, title, writer, content, regdate, cnt, uploadfile 
FROM board
WHERE seq = #{seq}

mapper.insert:

	SELECT nvl(MAX(seq),0) + 1 FROM BOARD

insert into board(seq, title, content, writer, regdate, cnt

	, uploadfile

) values (#{seq}, #{title}, #{content}, #{writer}, trunc(sysdate), 0 

	, #{uploadfile}

         )
